import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';

function BotSpecs() {
  const [bot, setBot] = useState(null);
  const { botId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/bots/${botId}`)
      .then(response => response.json())
      .then(data => setBot(data))
      .catch(error => console.error('Error fetching bot specs:', error));
  }, [botId]);

  const handleEnlist = () => {
    // Implement enlist functionality here
    console.log('Enlisting bot:', bot);
  };

  if (!bot) return <div>Loading...</div>;

  return (
    <div className="bot-specs">
      <h2>{bot.name}</h2>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <p>Class: {bot.bot_class}</p>
      <p>Catchphrase: {bot.catchphrase}</p>
      <Link to="/">Back to list</Link> {/* Link to go back to the list view */}
      <button onClick={handleEnlist}>Enlist</button> {/* Button to enlist the bot */}
    </div>
  );
}

export default BotSpecs;
