import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function BotSpecs() {
  const [bot, setBot] = useState(null);
  const [enlistedBots, setEnlistedBots] = useState([]); // Define enlistedBots state
  const { botId } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3001/bots/${botId}`)
      .then(response => response.json())
      .then(data => setBot(data))
      .catch(error => console.error('Error fetching bot specs:', error));
  }, [botId]);

  const handleEnlist = (enlistedBot) => {
    setEnlistedBots(prevBots => [...prevBots, enlistedBot]);
    fetch(`http://localhost:3001/enlistedBots`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(enlistedBot)
    })
    .then(response => response.json())
    .then(data => console.log('Bot enlisted:', data))
    .catch(error => console.error('Error enlisting bot:', error));
  };

  if (!bot) return <div>Loading...</div>;

  return (
    <div className="bot-specs">
      <h2>{bot.name}</h2>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <button onClick={() => handleEnlist(bot)}>Enlist</button>
    </div>
  );
}

export default BotSpecs;
